import React, { useState } from "react";

const TextFollow = ({ title, id, url }) => {
  const [hover, setHover] = useState(false);
  const getId = document.getElementById(id);

  const styles = {
    img: {
      width: "200px",
      height: "200px",
      objectFit: "contain",
    },
    text: {
      fontSize: "50px",
      fontWeight: "bold",
      cursor: "pointer",
      marginInline: "10px",
    },
    container: {
      padding: "20px",
    },
  };

  const onHandleMove = (ev) => {
    getId.style.top = `${ev.clientY}px`;
    getId.style.left = `${ev.clientX}px`;
    getId.style.transform = `translate(-50%,-50%)`;
  };

  return (
    <div
      style={styles.container}
      onMouseEnter={() => setHover(true)}
      onMouseOut={() => setHover(false)}
      onMouseMove={onHandleMove}
    >
      {/* <div id={id} style={styles.text}> */}
      <div id={id} className="acab">
        {title}
      </div>
    </div>
  );
};

export default TextFollow;
