import React from "react";
import Imagefollow from "react-image-follow";

const Banner = ({ isLightTheme }) => {
  const bannerTitle = document.getElementById("banner-title");
  const imgUrl =
    "https://images.unsplash.com/photo-1643101808398-ab1817b74013?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1170&q=80";

  const onHandleMove = (ev) => {
    console.log(ev.clientY, ev.clientX);
    bannerTitle.style.position = "absolute";
    bannerTitle.style.top = `${ev.clientY}px`;
    bannerTitle.style.left = `${ev.clientX}px`;
    bannerTitle.style.transform = `translate(-50%,-50%)`;
  };

  return (
    <div className="box">
      <div
        onMouseMove={onHandleMove}
        className={`animate-container ${isLightTheme ? "light" : "dark"}`}
      >
        <div id="banner-title" className="text-animate">
          TEST
        </div>
        {/* <img src={imgUrl} alt="img" className="img-animate" /> */}
        <Imagefollow src={imgUrl} alt="img" className="img-animate" />
      </div>
    </div>
  );
};

export default Banner;
