import React, { useState } from "react";
import Banner from "../components/Banner";
import Header from "../components/Header";
import ImageFollow from "react-image-follow";

const Home = () => {
  const [isLightTheme, setIsLightTheme] = useState(false);
  const [hover, setHover] = useState(false);

  const styles = {
    container: {
      background: isLightTheme ? "white" : "black",
      minHeight: "100vh",
    },
  };
  const src =
    "https://github.com/image-component/gallery/blob/main/girl/1.jpg?raw=true";

  document.addEventListener(
    "mousemove",
    function (ev) {
      document.getElementById("acab").style.transform =
        "translateY(" + (ev.clientY - 80) + "px)";
      document.getElementById("acab").style.transform +=
        "translateX(" + (ev.clientX - 100) + "px)";
    },
    false
  );

  return (
    <div style={styles.container}>
      <Header isLightTheme={isLightTheme} setIsLightTheme={setIsLightTheme} />
      <Banner isLightTheme={isLightTheme} />
      <marquee className="marquee">
        ORI AND FRIENDS ORI AND FRIENDS ORI AND FRIENDS ORI AND FRIENDS ORI AND
        FRIENDS ORI AND FRIENDS ORI AND FRIENDS
      </marquee>

      <div
        className="demo"
        onMouseEnter={() => setHover(true)}
        onMouseLeave={() => setHover(false)}
      ></div>

      <div id="acab">
        <img src="https://upload.wikimedia.org/wikipedia/commons/thumb/b/b1/Anarchist_black_cat.svg/150px-Anarchist_black_cat.svg.png"></img>
      </div>
    </div>
  );
};

export default Home;
