import React, { useState } from "react";

const ImageFollow = ({ id, imgUrl }) => {
  const [hover, setHover] = useState(false);
  return (
    <div
      className="image-follow-container"
      onMouseEnter={() => setHover(true)}
      onMouseLeave={() => setHover(false)}
      onMouseMove={(ev) => {
        document.getElementById(id).style.transform = `translateY(${
          ev.clientY - 425
        }px)`;
        document.getElementById(id).style.transform += `translateX(${
          ev.clientX - 100
        }px)`;
      }}
    >
      <div id={id} className="acab">
        <img src={imgUrl} />
      </div>
    </div>
  );
};

export default ImageFollow;
