import React, { useState } from "react";

const ImageFollow = ({ id, imgUrl }) => {
  const [hover, setHover] = useState(false);
  console.log({ hover });
  const styles = {
    img: {
      width: "200px",
      height: "200px",
      objectFit: "contain",
    },
    text: {
      fontSize: "50px",
      cursor: "pointer",
      display: "block",
    },
    textBox: {
      width: "100px",
      height: "50px",
    },
  };

  const onHandleMove = (ev) => {
    document.getElementById(id).style.transform = `translate(${
      ev.clientX - 100
    }px, ${ev.clientY - 425}px)`;
  };

  return (
    <span>
      <div style={styles.textBox}>
        <div
          style={styles.text}
          onMouseEnter={() => setHover(true)}
          onMouseOut={() => setHover(false)}
          onMouseMove={onHandleMove}
        >
          asdasd
        </div>
      </div>
      <div id={id} className="acab">
        {hover ? <img src={imgUrl} style={styles.img} /> : ""}
      </div>
    </span>
  );
};

export default ImageFollow;
