import React, { useState } from "react";
import Banner from "../components/Banner";
import Header from "../components/Header";

const Home = () => {
  const [isLightTheme, setIsLightTheme] = useState(false);

  const styles = {
    container: {
      background: isLightTheme ? "white" : "black",
      minHeight: "100vh",
    },
  };

  return (
    <div style={styles.container}>
      <Header isLightTheme={isLightTheme} setIsLightTheme={setIsLightTheme} />
      <Banner isLightTheme={isLightTheme} />
      <marquee className="marquee">
        ORI AND FRIENDS ORI AND FRIENDS ORI AND FRIENDS ORI AND FRIENDS ORI AND
        FRIENDS ORI AND FRIENDS ORI AND FRIENDS
      </marquee>

      <div
        id="follow"
        onMouseEnter={() => {
          $(document).mousemove(function (e) {
            $("#follow").css({
              left: e.pageX,
              top: e.pageY,
            });
          });
        }}
      >
        <img src="https://placekitten.com/96/140" />
        <br>Kitteh</br>
      </div>
    </div>
  );
};

export default Home;
