import React, { useState } from "react";

const ImageFollow = ({ id, imgUrl }) => {
  const [hover, setHover] = useState(false);

  //   const getImgDims = () => {
  //     var myImg = new Image();
  //     myImg.src = imgUrl;
  //     var currWidth = myImg.clientWidth;
  //     var currHeight = myImg.clientHeight;
  //     alert(
  //       "Current width=" + currWidth + ", " + "Original height=" + currHeight
  //     );
  //   };

  const styles = {
    img: {
      width: "200px",
      height: "200px",
      objectFit: "contain",
    },
    text: {
      fontSize: "50px",
    },
  };

  const onHandleMove = (ev) => {
    console.log(ev);
    document.getElementById(id).style.transform = `translate(${
      ev.clientX - 100
    }px, ${ev.clientY - 425}px)`;
  };

  return (
    <div className="image-follow-container">
      <span
        onMouseEnter={() => setHover(true)}
        onMouseLeave={() => setHover(false)}
        onMouseMove={onHandleMove}
        style={styles.text}
      >
        TEST
      </span>
      <div id={id} className="acab">
        <img src={imgUrl} style={styles.img} />
      </div>
    </div>
  );
};

export default ImageFollow;
