import React, { useState } from "react";

const ImageFollow = ({ title, id, imgUrl, url }) => {
  const [hover, setHover] = useState(false);

  const styles = {
    img: {
      width: "200px",
      height: "200px",
      objectFit: "contain",
    },
    text: {
      fontSize: "50px",
      cursor: "pointer",
    },
  };

  const onHandleMove = (ev) => {
    document.getElementById(id).style.top = `${ev.clientY}px`;
    document.getElementById(id).style.left = `${ev.clientX}px`;
    // document.getElementById(id).style.transform = `translate(${
    //   ev.clientX - 400
    // }px, ${ev.clientY - 400}px)`;
  };

  return (
    <div className="position-relative h-100">
      <span
        style={styles.text}
        onMouseEnter={() => setHover(true)}
        onMouseOut={() => setHover(false)}
        onMouseMove={onHandleMove}
      >
        <a className="text-hover" href={url}>
          {title}
        </a>
      </span>

      <div id={id} className="acab">
        {hover ? <img src={imgUrl} style={styles.img} /> : ""}
      </div>
    </div>
  );
};

export default ImageFollow;
